INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9780781763066', 'Biomechanical Basis of Human Movement', NULL, 'Biomechanical Basis of Human Movement integrates basic anatomy, physics, calculus, and physiology for the study of human movement. The book provides a uniquely quantitative approach to biomechanics, and is organized into three parts: Foundations of Human Movement, Functional Anatomy, and Mechanical Analysis of Human Motion. New to this edition: basic mathematics information, increased practical applications, and a new chapter on emphasizing techniques for measuring the strength of human tissue. Now every copy of the book comes with Innovision Systems'' MaxTRAQ software specially customized for Biomechanical Basis of Human Movement, Second Edition. This downloadable motion analysis software offers you an easy to use tool to track data and analyze various motions selected by the authors.', 486, 'BOOK', NULL, NULL, 'Lippincott Williams & Wilkins', '2006-10-01', 'http://books.google.com/books/content?id=WuWKRc2jZ5AC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
