INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9780735669956', 'Training Kit Exam 70-462', NULL, 'EXAM PREP GUIDE Ace your preparation for the skills measured by Exam 70-462--and on the job--with this official Microsoft study guide. Work at your own pace through a series of lessons and reviews that fully cover each exam objective. Then, reinforce and apply what you''ve learned through real-world case scenarios and practice exercises. Maximize your performance on the exam by mastering the skills and experience measured by these objectives: Install and configure SQL Server Maintain instances and databases Optimize and troubleshoot SQL Server Manage data Implement security Implement high availability PRACTICE TESTS A Note Regarding Practice Tests Assess your skills with practice tests. You can work through hundreds of questions using multiple testing modes to meet your specific learning needs. You get detailed explanations for right and wrong answers--including a customized learning path that describes how and where to focus your studies. For customers who purchase an ebook version of this title, instructions for downloading the CD files can be found in the ebook.', 932, 'BOOK', NULL, NULL, 'Microsoft Press', '2012-01-01', 'http://books.google.com/books/content?id=JoZ2CAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9781118058787', 'Beginning Access 2003 VBA', NULL, 'What is this book about? Written by an Access programmer with more than 10 years of VBA experience, this is the perfect guide for Access users who are ready to take their databases to the next level, or for programmers who are new to Access or VBA. Veteran Access developer Denise Gosnell shows readers the ins and outs of Access VBA and provides plenty of source code, and fully developed sample applications to guide you along the way. Not only do readers learn to build "stand-alone" desktop applications, but readers also learn how to integrate Access applications with Web Services, and SQL Server.', 544, 'BOOK', NULL, NULL, 'John Wiley & Sons', '2011-02-09', 'http://books.google.com/books/content?id=z2aoFGg1HFAC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
