INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('uKNQAAAAMAAJ', 'MySQL Clustering', NULL, 'Clustering is the final component of MySQL that makes it truly enterprise-level and able to compete fully with proprietary databases such as Oracle and Microsoft SQL Server. The increased number of high-demand, high-productivity corporations and institutions choosing MySQL, including MIT, the Department of Homeland Security, NASA, and Nokia, to name a few, need the benefit of clustering databases for high performance and scalability. MySQL AB has stepped up to the plate to offer advanced, high-availability, reliable clustering. As this demand has increased, so has the need for information. MySQL Clustering offers thorough, authoritative instruction on setting up and administering a MySQL Cluster from the developers of the cluster itself. You will learn about everything from installation and configuration to performance and troubleshooting in this authoritative reference guide to MySQL clustering.', 226, 'BOOK', NULL, NULL, 'Sams Publishing', '2006-01-01', 'http://books.google.com/books/content?id=uKNQAAAAMAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('juhQAAAAMAAJ', 'MySQL Language Reference', NULL, 'With more than 4 million active installations, MySQL is the world''s most popular open-source database. Known for its speed, high reliability, and ease of use, MySQL is rapidly becoming a low-cost alternative to high-priced, high-maintenance database systems from Oracle, IMB and Microsoft-and it already has more users than any of these more established proprietary databases. The "MySQL Language Reference" is the only official guide to the MySQL language and programming APIs. Written by the creators of MySQL, and edited by the highly respected MySQL authors, the "MySQL Language Reference" is designed for database administrators and programmers who need a reliable, detailed reference to every part of the MySQL language and all the available APIs.', 508, 'BOOK', NULL, NULL, 'Unknown Publisher', '2005-01-01', 'http://books.google.com/books/content?id=juhQAAAAMAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api');
