INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9791188427239', '막힘없이 PostgreSQL', 'PostgreSQL 성능관리를 위한 핵심 매커니즘', '이 책은 PostgreSQL 초·중급 독자를 대상으로 성능 관리를 위해 반드시 알아야 할 핵심 주제들로 구성되어 있습니다. 아키텍처, 트랜잭션, MVCC, Vacuum, 락(Lock), SQL 실행 계획 등 PostgreSQL의 핵심 기능을 심도 있게 다루었습니다. 단순히 이론만 서술하는 것을 넘어, 다양한 테스트와 검증을 통해 내부 작동 원리를 명확히 이해할 수 있도록 집필하였습니다. 이러한 구성 덕분에 막힘없이 PostgreSQL에 도전할 수 있기를 기대하며 책 제목을 “막힘없이 PostgreSQL”로 정하게 되었습니다. 특히 기술적으로 어렵다고 생각되는 내용을 독자들이 쉽게 이해할 수 있도록 다양한 그림과 시각 자료를 활용하였으며, 원리를 직접 체험해 볼 수 있도록 실습 예제와 스크립트도 함께 제공하였습니다. 참고로 모든 테스트는 PostgreSQL 14 버전 이상에서 수행할 수 있도록 구성했습니다.', 402, 'BOOK', 12600, 'KRW', '엑셈', '2025-01-23', 'http://books.google.com/books/content?id=Ly9BEQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('0735712573', 'PostgreSQL', 'A Comprehensive Guide to Building, Programming, and Administering PostgresSQL Databases', '"PostgreSQL" leads users through the internals of an open-source database. Throughout the book are explanations of data structures and algorithms, each backed by a concrete example from the actual source code. Each section contains information about performance implications, debugging techniques, and pointers to more information (on the Web and in book form).', 818, 'BOOK', NULL, NULL, 'Sams Publishing', '2003-01-01', 'http://books.google.com/books/content?id=gkQVL9pyFVYC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9781430200185', 'Beginning Databases with PostgreSQL', 'From Novice to Professional', 'PostgreSQL is arguably the most powerful open-source relational database system. It has grown from academic research beginnings into a functionally-rich, standards-compliant, and enterprise-ready database used by organizations all over the world. And it''s completely free to use. Beginning Databases with PostgreSQL offers readers a thorough overview of database basics, starting with an explanation of why you might need to use a database, and following with a summary of what different database types have to offer when compared to alternatives like spreadsheets. You''ll also learn all about relational database design topics such as the SQL query language, and introduce core principles including normalization and referential integrity. The book continues with a complete tutorial on PostgreSQL features and functions and include information on database construction and administration. Key features such as transactions, stored procedures and triggers are covered, along with many of the capabilities new to version 8. To help you get started quickly, step-by-step instructions on installing PostgreSQL on Windows and Linux/UNIX systems are included. In the remainder of the book, we show you how to make the most of PostgreSQL features in your own applications using a wide range of programming languages, including C, Perl, PHP, Java and C#. Many example programs are presented in the book, and all are available for download from the Apress web site. By the end of the book you will be able to install, use, and effectively manage a PostgreSQL server, design and implement a database, and create and deploy your own database applications.', 650, 'BOOK', NULL, NULL, 'Apress', '2006-11-03', 'http://books.google.com/books/content?id=Nc5ZT2X5mOcC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('0672322609', 'PostgreSQL Developer''s Handbook', NULL, '"PostgreSQL Developer''s Handbook" provides a complete overview of the PostgreSQL database server and extensive coverage of its core features, including object orientation, PL/SQL, and the most important programming interfaces. The authors introduce the reader to the language and syntax of PostgreSQL and then move quickly into sophisticated programming topics.', 772, 'BOOK', NULL, NULL, 'Sams Publishing', '2002-01-01', 'http://books.google.com/books/content?id=LjXBeAWM-KgC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9781430201366', 'Beginning PHP and PostgreSQL 8', 'From Novice to Professional', 'Beginning PHP and PostgreSQL 8 delves into some of the most popular open-source web development technologies, the PostgreSQL 8 database server and PHP 5 scripting language. You''ll learn to reap the benefits of these core technologies by using them in unison to create dynamic, data-driven web applications. This is an ideal read if you are a web designer, programmer, hobbyist, or novice who wants to create applications with PHP 5 and PostgreSQL 8.', 863, 'BOOK', NULL, NULL, 'Apress', '2006-11-21', 'http://books.google.com/books/content?id=BiRC4JtQzFIC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9781596821590', 'PostgreSQL 8.4 Official Documentation - Volume II. Server Administration', NULL, 'Welcome to the "PostgreSQL 8.4 Official Documentation - Volume II. Server Administration"! After many years of development, PostgreSQL has become feature-complete in many areas. This release shows a targeted approach to adding features (e.g., authentication, monitoring, space reuse), and adds capabilities defined in the later SQL standards.', 436, 'BOOK', NULL, NULL, 'Fultus Corporation', '2009-08-01', 'http://books.google.com/books/content?id=EwSt1gAjRjgC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
