INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9791188393473', '유레카 NLP', '인생 기본기를 만들어주는', '사람은 자기 자신을 프로그램할 수 있는 무한한 가능성의 존재로 태어난다. 하지만 수없이 많은 환경들을 받아들이고 나름대로 자기 자신을 프로그램해 가며 자신의 삶을 왜곡시키기도 한다. NLP(Neuro-Linguistic-Programming)란 신경 언어를 기반으로 한 프로그램으로 자기 자신이 사용하는 언어를 바꾸어 줌으로써 원하는 자아상으로 프로그램할 수 있다는 것으로, 사람은 바라보는 세상으로 자기 자신을 프로그램하고 있다는 것이다. NLP는 이론을 내세우면 정말 어려운 부분이지만 바로 실생활에 적용하면 너무도 쉽고 빠르게 자기 자신을 만들어갈 뿐만 아니라 타인에게도 지대한 영향을 미칠 수 있다. 독자 여러분은 [유레카 NLP]를 통해 NLP를 이해하고, 빠르게 감정 정복, 경제력 정복, 인간관계 정복, 시간 정복, 건강 정복에 이르기까지 모든 환경이 자기 자신이 원하는 형태로 만들어지게 될 것이다.', 324, 'BOOK', 13000, 'KRW', '한국평생교육원', '2021-05-20', 'http://books.google.com/books/content?id=re69EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9780062085429', 'NLP', 'The New Technology of Achievement', 'NLP has already helped millions of people overcome fears, increase confidence, enrich relationships, and achieve greater sucess. Now the NLP Comprehensive Training Team has written a book that reveals how to use this breakthrough technology to achieve whatever you want. Short for neuro-linguistic programming, NLP is a revolutionary approach to human communication and development. In NLP: The New Technology of Achievement, you''ll be guided step-by-step through specific programs for learning the characeristics of top achievers and creating a blueprint for unlimited sucess. Plus, an all-new twenty-one-day program created especially for this book provides you with the essential skills you''ll need to achieve peak performance in business and life.', 409, 'BOOK', 4026, 'KRW', 'Harper Collins', '2011-08-16', 'http://books.google.com/books/content?id=7nkfUssmxC0C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
