INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9781118003152', 'Mastering Hyper-V Deployment', NULL, 'The only book to take an in-depth look at deploying Hyper-V Now in its second generation, the popular Hyper-V boasts technical advances that create even more dynamic systems than ever before. This unique resource serves an authoritative guide to deploying Windows Server 2008 R2 Hyper-V comprehensively. Step-by-step instructions demonstrate how to design a Hyper-V deployment, build a Hyper-V host environment, and design a management system with System Center Virtual Machine Manager 2008 R2. Features real-world examples that show you how to design a Hyper-V deployment, build a Hyper-V host environment, and design a management system Walks you through incorporating System Center Operations Manager 2008 R2, System Center Data Protection Manager 2010, and System Center Essentials 2010 Offers authoritative coverage of Hyper-V security, business continuity, and an array of VM roles, including domain controllers, Exchange Server, SQL Server, and System Center This in-depth guide is the ultimate resource for system administrators, engineers, and architects, and IT consultants deploying Hyper-V.', 602, 'BOOK', NULL, NULL, 'John Wiley & Sons', '2010-11-09', 'http://books.google.com/books/content?id=Q5NMwwGDT_8C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9780080555713', 'The Official CHFI Study Guide (Exam 312-49)', 'for Computer Hacking Forensic Investigator', 'This is the official CHFI (Computer Hacking Forensics Investigator) study guide for professionals studying for the forensics exams and for professionals needing the skills to identify an intruder''s footprints and properly gather the necessary evidence to prosecute. The EC-Council offers certification for ethical hacking and computer forensics. Their ethical hacker exam has become very popular as an industry gauge and we expect the forensics exam to follow suit. Material is presented in a logical learning sequence: a section builds upon previous sections and a chapter on previous chapters. All concepts, simple and complex, are defined and explained when they appear for the first time. This book includes: Exam objectives covered in a chapter are clearly explained in the beginning of the chapter, Notes and Alerts highlight crucial points, Exam''s Eye View emphasizes the important points from the exam''s perspective, Key Terms present definitions of key terms used in the chapter, Review Questions contains the questions modeled after real exam questions based on the material covered in the chapter. Answers to the questions are presented with explanations. Also included is a full practice exam modeled after the real exam. - The only study guide for CHFI, provides 100% coverage of all exam objectives. - CHFI Training runs hundreds of dollars for self tests to thousands of dollars for classroom training.', 961, 'BOOK', 61596, 'KRW', 'Elsevier', '2011-08-31', 'http://books.google.com/books/content?id=8hujhG574csC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9783540890959', 'Critical Information Infrastructures Security', 'Second International Workshop, CRITIS 2007, Benalmadena-Costa, Spain, October 3-5, 2007', 'This book constitutes the thoroughly refereed post-conference proceedings of the Second International Workshop on Critical Information Infrastructures Security, CRITIS 2007, held in Benalmadena-Costa, Spain, in October 2007 in conjunction with ITCIP 2007, the first conference on Information Technology for Critical Infrastructure Protection. The 29 revised full papers presented were carefully reviewed and selected from a total of 75 submissions. The papers address all security-related heterogeneous aspects of critical information infrastructures and are orgaized in topical sections on R&D agenda, communication risk and assurance, code of practice and metrics, information sharing and exchange, continuity of services and resiliency, SCADA and embedded security, threats and attacks modeling, as well as information exchange and modeling.', 374, 'BOOK', NULL, NULL, 'Springer Science & Business Media', '2008-11-06', 'http://books.google.com/books/content?id=aQTbm1uVLhcC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
