INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9781317213413', 'Construction Law', 'Volume III', 'Now in its second edition, Construction Law is the standard work of reference for busy construction law practitioners, and it will support lawyers in their contentious and non-contentious practices worldwide. Published in three volumes, it is the most comprehensive text on this subject, and provides a unique and invaluable comparative, multi-jurisdictional approach. This book has been described by Lord Justice Jackson as a "tour de force", and by His Honour Humphrey LLoyd QC as "seminal" and "definitive". This new edition builds on that strong foundation and has been fully updated to include extensive references to very latest case law, as well as changes to statutes and regulations. The laws of Hong Kong and Singapore are also now covered in detail, in addition to those of England and Australia. Practitioners, as well as interested academics and post-graduate students, will all find this book to be an invaluable guide to the many facets of construction law.', 1555, 'BOOK', 1016666, 'KRW', 'Taylor & Francis', '2016-07-22', 'http://books.google.com/books/content?id=uC9EDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9783030711870', 'Intelligent Systems Design and Applications', '20th International Conference on Intelligent Systems Design and Applications (ISDA 2020) held December 12-15, 2020', 'This book highlights recent research on intelligent systems and nature-inspired computing. It presents 130 selected papers from the 19th International Conference on Intelligent Systems Design and Applications (ISDA 2020), which was held online. The ISDA is a premier conference in the field of computational intelligence, and the latest installment brought together researchers, engineers and practitioners whose work involves intelligent systems and their applications in industry. Including contributions by authors from 40 countries, the book offers a valuable reference guide for all researchers, students and practitioners in the fields of Computer Science and Engineering.', 1440, 'BOOK', 319446, 'KRW', 'Springer Nature', '2021-06-02', 'http://books.google.com/books/content?id=JH0xEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
