INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9791188427123', 'Java Performance Fundamental', NULL, 'JVM의 체계적인 지식을 제공하여 Java를 사용하는 분들께 성능에 대한 기반지식을 쌓도록 돕는다. 그 동안 잘 다루어 지지 않은 JVM의 메커니즘에 대한 이야기로 가득차 있다. Java 개발자, 운영자, 성능 관리자들이 골머리를 썩여왔던 메모리 문제, Garbage Collection에 대한 주제는 물론이고 JIT Compiler 등 용어로 치부했던 것들에 대한 자세한 설명을 수록하였다. 그리고 JDBC에 대한 설명과 함께 Oracle을 Datasource로 이용하여 SQL을 수행할 때 WAS와 같은 Java Application과 Oracle에는 어떤 일이 벌어지는 지를 테스트를 통해 상세하게 밝혔다. 또한 이를 바탕으로 Oracle JDBC의 성능을 개선시키는 방안도 모색하고 있어, Java Perdormance 를 위한 좋은 안내자가 되는 책이다.', 522, 'BOOK', 21000, 'KRW', '엑셈', '2023-11-06', 'http://books.google.com/books/content?id=gLrlEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9781442962835', 'Competency-Based Performance Reviews', 'How to Perform Employee Evaluations the Fortune 500 Way', 'Competency-Based Performance Reviews offers you a new and more effective way to handle performance reviews and to coach your employees to emphasize the knowledge, skills, and abilities that they have and the organization needs. Most sophisticated U.S. and international employers are using competency-based systems to select and interview their employees, as well as evaluate the performance of those employees. Fortune 500 corporations such as American Express, Anheuser-Busch, Coca-Cola, Disney, Federal Express, IBM, Johnson & Johnson, and Pfizer are all looking for specific competencies. Competency-Based Performance Reviews includes sample phrases to use on reviews, as well as sample accomplishment statements to help employees write and improve their own.', 318, 'BOOK', NULL, NULL, 'ReadHowYouWant.com', '2009-04-02', 'http://books.google.com/books/content?id=_Wvzv3_qJdIC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('038724137X', 'Modeling Performance Measurement', 'Applications and Implementation Issues in DEA', 'This volume addresses advanced DEA methodology and techniques developed for modeling unique new performance evaluation issues. Many numerical examples, real management cases and verbal descriptions make it very valuable for researchers and practitioners.', 432, 'BOOK', NULL, NULL, 'Springer Science & Business Media', '2005-01-10', 'http://books.google.com/books/content?id=Cd_yajVoZz8C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('1412901553', 'Integrated Performance Management', 'A Guide to Strategy Implementation', 'Linking various disciplines and management functions, Integrated Performance Management provides the reader with a concrete framework to manage organizations successfully. The authors do not isolate a single strategy to manage performance. Instead, the book focuses on a range of strategies providing the reader with an introduction to each one. The concepts under analysis were developed through intense dialogue with business managers. While maintaining academic rigour, Integrated Performance Management presents ideas that students will find relevant outside of the classroom. Postgraduate and MBA students in a range of areas including strategy, accounting, finance, operations management, marketing, leadership and human resource management will find this book useful.', 356, 'BOOK', NULL, NULL, 'SAGE', '2004-12-23', 'http://books.google.com/books/content?id=q8f62nWP_cMC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('1457402777', 'Collected Thoughts on Teaching and Learning, Creativity and Horn Performance', NULL, 'Douglas Hill is professor of music and horn at the University of Wisconsin at Madison as well as a past President of the International Horn Society, and a respected teacher and clinician. The 27 chapters of Collected Thoughts cover topics ranging from getting started to preparing for college and professional auditions, and include other subjects such as composing and improvising. There are seven chapters on repertoire that include reviews of music and texts that are the most comprehensive of any horn (or other instrumental) text to date. The process of learning and teaching is extremely insightful for everyone, from the serious student to the most experienced instructor. This book is a must for anyone interested in the horn. It is invaluable!!', 224, 'BOOK', NULL, NULL, 'Alfred Music', '2000-01-01', 'http://books.google.com/books/content?id=sjqUmhvwzLwC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
