INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9780387095219', 'Value-Focused Business Process Engineering : a Systems Approach', 'with Applications to Human Resource Management', 'One of the keys to successful business process engineering is tight alignment of processes with organisational goals and values. Historically, however, it has always been difficult to relate different levels of organizational processes to the strategic and operational objectives of a complex organization with many interrelated and interdependent processes and goals. This lack of integration is especially well recognized within the Human Resource Management (HRM) discipline, where there is a clearly defined need for greater alignment of HRM processes with the overall organizational objectives. Value-Focused Business Process Engineering is a monograph that combines and extends the best on offer in Information Systems and Operations Research/Decision Sciences modelling paradigms to facilitate gains in both business efficiency and business effectiveness.', 271, 'BOOK', 79849, 'KRW', 'Springer Science & Business Media', '2008-12-16', 'http://books.google.com/books/content?id=dBus2eVk8AkC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('0714648450', 'Institutions and the Evolution of Modern Business', NULL, 'The nine contributions in this volume attempt to demonstrate the applications of institutional theory to business history and show how the wider study of the institutional environment is inseparable from the study of business. Contributions include an evaluation of community structure and competitive advantage within the Yorkshire woolen industry; a comparative view of financial reconstruction and industrial reorganization in British and Swedish institutions during the Inter-War period; and post- war strategic capitalism in Norway. Paper edition (unseen), $19.50. Distributed by ISBS. Annotation copyrighted by Book News, Inc., Portland, OR', 200, 'BOOK', NULL, NULL, 'Psychology Press', '1998-01-01', 'http://books.google.com/books/content?id=vrvhzg5JyEwC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9780198038412', 'Managing Business Complexity', 'Discovering Strategic Solutions with Agent-Based Modeling and Simulation', 'Agent-based modeling and simulation (ABMS), a way to simulate a large number of choices by individual actors, is one of the most exciting practical developments in business modeling since the invention of relational databases. It represents a new way to understand data and generate information that has never been available before--a way for businesses to view the future and to understand and anticipate the likely effects of their decisions on their markets and industries. It thus promises to have far-reaching effects on the way that businesses in many areas use computers to support practical decision-making. Managing Business Complexity is the first complete business-oriented agent-based modeling and simulation resource. It has three purposes: first, to teach readers how to think about ABMS, that is, about agents and their interactions; second, to teach readers how to explain the features and advantages of ABMS to other people and third, to teach readers how to actually implement ABMS by building agent-based simulations. It is intended to be a complete ABMS resource, accessible to readers who haven''t had any previous experience in building agent-based simulations, or any other kinds of models, for that matter. It is also a collection of ABMS business applications resources, all assembled in one place for the first time. In short, Managing Business Complexity addresses who needs ABMS and why, where and when ABMS can be applied to the everyday business problems that surround us, and how specifically to build these powerful agent-based models.', 326, 'BOOK', 149064, 'KRW', 'Oxford University Press', '2007-03-01', 'http://books.google.com/books/content?id=Y-0FeyoO4j0C&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
