INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9781351754804', 'The Mayan Languages', NULL, 'The Mayan Languages presents a comprehensive survey of the language family associated with the Classic Mayan civilization (AD 200–900), a family whose individual languages are still spoken today by at least six million indigenous Maya in Mexico, Guatemala, Belize, and Honduras. This unique resource is an ideal reference for advanced undergraduate and postgraduate students of Mayan languages and linguistics. Written by a team of experts in the field, The Mayan Languages presents in-depth accounts of the linguistic features that characterize the thirty-one languages of the family, their historical evolution, and the social context in which they are spoken. The Mayan Languages: provides detailed grammatical sketches of approximately a third of the Mayan languages, representing most of the branches of the family; includes a section on the historical development of the family, as well as an entirely new sketch of the grammar of "Classic Maya" as represented in the hieroglyphic script; provides detailed state-of-the-art discussions of the principal advances in grammatical analysis of Mayan languages; includes ample discussion of the use of the languages in social, conversational, and poetic contexts. Consisting of topical chapters on the history, sociolinguistics, phonology, morphology, syntax, semantics, discourse structure, and acquisition of the Mayan languages, this book will be a resource for researchers and other readers with an interest in historical linguistics, linguistic anthropology, language acquisition, and linguistic typology.', 790, 'BOOK', NULL, NULL, 'Taylor & Francis', '2017-05-12', 'http://books.google.com/books/content?id=CTUlDwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
INSERT IGNORE INTO book (isbn, title, subtitle, description, page_count, format, amount, currency, publisher, published_date, image_url) VALUES ('9783540705611', 'Software Engineering Research, Management and Applications', NULL, 'The 6th ACIS International Conference on Software Engineering, Research, Management and Applications (SERA 2008) was held in Prague in the Czech Republic on August 20 – 22. SERA ’08 featured excellent theoretical and practical contributions in the areas of formal methods and tools, requirements engineering, software process models, communication systems and networks, software quality and evaluation, software engineering, networks and mobile computing, parallel/distributed computing, software testing, reuse and metrics, database retrieval, computer security, software architectures and modeling. Our conference officers selected the best 17 papers from those papers accepted for presentation at the conference in order to publish them in this volume. The papers were chosen based on review scores submitted by members or the program committee, and underwent further rounds of rigorous review.', 251, 'BOOK', 143742, 'KRW', 'Springer', '2009-01-29', 'http://books.google.com/books/content?id=87ZqCQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api');
